import React from 'react'
import { Grid, } from '@material-ui/core';
import Controls from "../components/controls/Controls";
import { useForm, Form } from '../components/useForm';
// import * as employeeService from "../services/employeeService";


const genderItems = [
    { id: 'male', title: 'Male' },
    { id: 'female', title: 'Female' },
    { id: 'other', title: 'Other' },
]

const initialFValues = {
    name_first: '',
    name_middle:'',
    name_last:'',
    dob:'',
    phone1:'',
    phone1_type:'',
    phone1_text_yn:'',
    phone2_yn:'',
    phone2:'',
    phone2_type:'',
    phone2_text_yn:'',
    contact_besttime_survey:'',
    address1:'',
    address2:'',
    city: '',
    state:'',
    zipcode:'',
    voicemail_yn_survey:'',
    copd_yn_survey:'',
    copd_exacerbation_yn_survey:'',
    smoking_yn_survey:'',
    smoking_now_yn_survey:'',
    smoking_start_survey:'',
    smoking_stop_survey:'',
    smoking_amount_survey:'',
    rx_bp_yn_survey:'',
    rx_bp_survey:'',
    recruitment_source_survey:'',
    recruitment_source_other_survey:'',
    current_age:'',
    gender: 'male',
    race:'',
    ethnicity:'',
    email_yn:'',
    email:'',

    departmentId: '',
    hireDate: new Date(),
    isPermanent: false,
}

export default function PatientInfo() {

    const validate = (fieldValues = values) => {
        let temp = { ...errors }
        if ('fullName' in fieldValues)
            temp.fullName = fieldValues.fullName ? "" : "This field is required."
        if ('email' in fieldValues)
            temp.email = (/$^|.+@.+..+/).test(fieldValues.email) ? "" : "Email is not valid."
        if ('mobile' in fieldValues)
            temp.mobile = fieldValues.mobile.length > 9 ? "" : "Minimum 10 numbers required."
        if ('departmentId' in fieldValues)
            temp.departmentId = fieldValues.departmentId.length !== 0 ? "" : "This field is required."
        setErrors({
            ...temp
        })

        if (fieldValues === values)
            return Object.values(temp).every(x => x === "")
    }

    const {
        values,
        errors,
        setErrors,
        handleInputChange,
        resetForm
    } = useForm(initialFValues, true, validate);

    const handleSubmit = e => {
        e.preventDefault()
        if (validate()){
            // employeeService.insertEmployee(values)
            resetForm()
        }
    }

    return (
        <Form onSubmit={handleSubmit}>
            <Grid container>
                <Grid item xs={6}>
                    <Controls.Input
                        name="firstName"
                        label="First Name"
                        value={values.name_first}
                        onChange={handleInputChange}
                        error={errors.name_first}
                    />
                    <Controls.Input
                        name="middleName"
                        label="Middle Name"
                        value={values.name_middle}
                        onChange={handleInputChange}
                        error={errors.name_middle}
                    />
                    <Controls.Input
                        name="lastName"
                        label="Last Name"
                        value={values.name_last}
                        onChange={handleInputChange}
                        error={errors.name_last}
                    />

                </Grid>
                <Grid item xs={6}>

                    <div>
                        <Controls.Button
                            type="submit"
                            text="Submit" />
                        <Controls.Button
                            text="Reset"
                            color="default"
                            onClick={resetForm} />
                    </div>
                </Grid>
            </Grid>
        </Form>
    )
}
