import { MsalAuthProvider, LoginType } from 'react-aad-msal';

const config = {
    auth: {
    authority: 'https://login.microsoftonline.com/cef04b19-7776-4a94-b89b-375c77a8f936',
    clientId: '87ade1b2-8121-4af8-aeee-d2308e029e73',
    postLogoutRedirectUri: window.location.origin,
    redirectUri: window.location.origin,
    validateAuthority: true,
    navigateToLoginRequestUrl: true
    },
    cache: {
    cacheLocation: "localStorage",
    storeAuthStateInCookie: true
    }
};
// Authentication Parameters
export const authenticationParameters = {
    scopes: [
    `87ade1b2-8121-4af8-aeee-d2308e029e73/.default`
    ]
}
export const authenticationParametersGraph = {
    scopes: [
    'openid'
    ]
}
// Options
export const options = {
loginType: LoginType.Redirect,
tokenRefreshUri: window.location.origin
}
export const authProvider = new MsalAuthProvider(config, authenticationParameters, options)
